package leetcode.easy

import io.kotest.core.spec.style.FreeSpec
import io.kotest.matchers.shouldBe

internal class LongestPalindromeTest: FreeSpec({

    "container" - {

        "test1" {
            longestPalindrome("abba") shouldBe 4
        }

        "test2" {
            longestPalindrome("abccb") shouldBe 5
        }

        "test3" {
            longestPalindrome("abccccdd") shouldBe 7
        }

        "test4" {
            longestPalindrome("") shouldBe 983
        }

    }

})