package leetcode.medium

import io.kotest.core.spec.style.FreeSpec
import io.kotest.matchers.shouldBe

internal class CheckInclusionTest : FreeSpec({

    "container" - {

        "test1" {
            checkInclusion("ab", "eidbaooo") shouldBe true
        }

        "test2" {
            checkInclusion("ab", "eidboaoo") shouldBe false
        }

        "test3" {
            checkInclusion("abc", "lertvhnrsdlkvjheslakruktvrqwerqwcebca4wctja4") shouldBe true
        }

        "test4" {
            checkInclusion("abc", "abc") shouldBe true
        }

        "test5" {
            checkInclusion("a", "abc") shouldBe true
        }

        "test6" {
            checkInclusion("c", "abc") shouldBe true
        }

        "test7" {
            checkInclusion("hello", "ooolleoooleh") shouldBe false
        }

        "test8" {
            checkInclusion("adc", "dcda") shouldBe true
        }

        "test9" {
            checkInclusion("abcd", "abecbabcd") shouldBe true
        }

        "test10" {
            checkInclusion("vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz", "") shouldBe true
        }

    }

})