package leetcode.medium

import io.kotest.core.spec.style.FreeSpec
import io.kotest.matchers.shouldBe

internal class LongestPalindromeTest: FreeSpec({

    "container" - {

        "test01" {
            longestPalindrome("babad") shouldBe "bab"
        }

        "test02" {
            longestPalindrome("cbbd") shouldBe "bb"
        }

        "test03" {
            longestPalindrome("a") shouldBe "a"
        }

        "test04" {
            longestPalindrome("bb") shouldBe "bb"
        }

        "test05" {
            longestPalindrome("") shouldBe ""
        }

        "test06" {
            longestPalindrome("ab") shouldBe "a"
        }

        "test07" {
            longestPalindrome("") shouldBe ""
        }

    }

})